import React from 'react';
import { withStyles } from '@material-ui/core/styles';
import ListItem from "@material-ui/core/ListItem";
import Divider from "@material-ui/core/Divider";
import ListItemText from "@material-ui/core/ListItemText";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import Collapse from "@material-ui/core/Collapse";

const styles = {
    root: {
        margin: '0px 0px 0px 0px',
        display: 'flex'
        // textAlign: 'center',
    },
    avatar: {
        margin: '10px 10px 10px 10px',
        display: 'flex',
        width: 240,
        height: 200,
    },
    card_content: {
        justify: 'center',
    }
};

export class BreederInfoCard extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            readMore: false,
        };

        this.handleReadMoreClick = this.handleReadMoreClick.bind(this);
    }

    handleReadMoreClick() {
        this.setState({
            readMore: !this.state.readMore
        });
    }

    render() {
        const { classes } = this.props;
        return (
            <ListItem alignItems="flex-start">
                <Card className={classes.root}>
                    <CardMedia
                        className={classes.avatar}
                        title="Breeder Image"
                        image="https://homepages.cae.wisc.edu/~ece533/images/cat.png"
                    // <Avatar variant='square' alt="Remy Sharp" src="https://topics.amcham.com.tw/wp-content/uploads/2016/07/biodiversity6-1.jpg" />
                    />
                    <div className={classes.card_content}>
                        <CardContent>
                            <Typography component="h5" variant="h5">
                                SnoValley Collies
                            </Typography>
                            <Typography variant="body1" color="textSecondary">
                                Coming from only the purest and finest of bloodlines, we have carefully selected which dogs we breed. We are intentional ...
                            </Typography>
                        </CardContent>
                    </div>

                    <Collapse in={this.state.readMore} timeout="auto" unmountOnExit>
                        <CardContent>
                            <Typography paragraph>Method:</Typography>
                            <Typography paragraph>
                                Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10
                                minutes.
                            </Typography>
                            <Typography paragraph>
                                Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high
                                heat. Add chicken, shrimp and chorizo, and cook, stirring occasionally until lightly
                                browned, 6 to 8 minutes. Transfer shrimp to a large plate and set aside, leaving chicken
                                and chorizo in the pan. Add pimentón, bay leaves, garlic, tomatoes, onion, salt and
                                pepper, and cook, stirring often until thickened and fragrant, about 10 minutes. Add
                                saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
                            </Typography>
                            <Typography paragraph>
                                Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook
                                without stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to
                                medium-low, add reserved shrimp and mussels, tucking them down into the rice, and cook
                                again without stirring, until mussels have opened and rice is just tender, 5 to 7
                                minutes more. (Discard any mussels that don’t open.)
                            </Typography>
                            <Typography>
                                Set aside off of the heat to let rest for 10 minutes, and then serve.
                            </Typography>
                        </CardContent>
                    </Collapse>
                </Card>
            </ListItem>
            //   <Divider variant="inset" component="li" />
        );

    }
}

export default withStyles(styles)(BreederInfoCard);
